    int fibon(int n) {
        if (n == 1 || n == 2) return 1; // Fix for base case
        else if (n <= 0) return 0;

        int& ans = dp[n];
        if (ans != -1) return ans;

        return ans = fibon(n-1) + fibon(n-2); // Fix for the Fibonacci sequence calculation
    }

    int fib(int n){
        memset(dp, -1, sizeof(dp)); // Initialize dp array with -1
        int ans = fibon(n);
        return ans;
    }
};

