#include <stack>

class Solution {
public:
    vector<int> nextSmallNumberIndex(vector<int>& v){
        int n = v.size(), num = 0;
        vector<int> ans(n);
        stack<int> s;
        for (int i = n - 1; i >= 0; i--) {
            while (!s.empty() && v[s.top()] >= v[i]) {
                s.pop();
            }
            ans[i] = s.empty() ? -1 : s.top();
            s.push(i);
        }
        return ans;
    }

    vector<int> prevSmallNumberIndex(vector<int>& v){
        int n = v.size(), num = 0;
        vector<int> ans(n);
        stack<int> s;
        for (int i = 0; i < n; i++) {
            while (!s.empty() && v[s.top()] >= v[i]) {
                s.pop();
            }
            ans[i] = s.empty() ? -1 : s.top();
            s.push(i);
        }
        return ans;
    }

    int largestRectangleArea(vector<int>& heights) {
        int n = heights.size(), h, l, area = 0, curArea;
        vector<int> next = nextSmallNumberIndex(heights);
        vector<int> prev = prevSmallNumberIndex(heights);
        for (int i = 0; i < n; i++) {
            h = heights[i];
            if (next[i] == -1) next[i] = n;
            l = next[i] - prev[i] - 1;
            curArea = l * h;
            area = max(area, curArea);
        }
        return area;        
    }
};

