        this->n = n;
        path_leaf.resize(n);
        belong.resize(n, -1);
        ind.resize(n, -1);
        leaf.resize(n, 1);

        for (int i = 0; i < n; i++) {
            if (parent[i] != -1)   
                leaf[parent[i]] = false; // Bug: Set leaf to false when parent is present
        }

        for (int i = 0; i < n; i++) {
            if (!leaf[i]) continue; // If node is not a leaf, skip it
            int curr = i;
            belong[curr] = curr;
            while (curr != -1) {
                curr = parent[curr];
                path_leaf[i].push_back(curr); // Push current node to the path of its ancestors
                if (curr != -1) {
                    belong[curr] = i; // Set ancestor's belonging to current node
                    ind[curr] = path_leaf[i].size() - 1; // Update index in path for each ancestor
                }
            }
        }
    }

    int getKthAncestor(int node, int k) {
        return path_leaf[belong[node]][max(k + ind[node], (int)path_leaf[belong[node]].size() - 1)]; // Return the kth ancestor of the given node
