        for (int j = 0; j < grid.size(); j++) {
            grid[j][c] = 1 - grid[j][c];
        }
    }

    int matrixScore(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        for (int i = 0; i < n; i++) {
            if (grid[i][0] == 0) {    
                fliprow(i, grid);
            }
        }

        for (int j = 0; j < m; j++) {
            int cnt0 = 0, cnt1 = 0;

            for (int i = 0; i < n; i++) {
                if (grid[i][j] == 1) 
                    cnt1++;
                else 
