class FrequencyTracker {
public:
    unordered_map<int, int> x, y;

    void add(int number) {
        if (x.find(number) == x.end()) { // Check if the number exists in the map
            x[number] = 1; // If not, set its frequency to 1
        } else {
            int freq = ++x[number]; // Increase the frequency by 1
            y[freq - 1]--; // Decrease the count of the previous frequency in 'y' map
        }
        y[x[number]]++; // Increase the count of the new frequency in 'y' map
    }

    void deleteOne(int number) {
        if (x.find(number) != x.end()) { // Check if the number exists in the map
            int freq = --x[number]; // Decrease the frequency by 1
            y[freq + 1]--; // Decrease the count of the new frequency in 'y' map

            if (freq == 0) { // If the number's frequency reaches zero, remove it from the map
                x.erase(number);
            } else {
                y[freq]++; // Increase the count of the new frequency in 'y' map
            }
        }
    }

    bool hasFrequency(int frequency) {
        if (y.find(frequency) != y.end() && y[frequency] > 0) return true; // Check if there are numbers with the specified frequency
        return false;
    }
};

