class Solution {
public:
    int minCost(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> dp(n+1, INT_MAX);
        dp[0] = 0;

        for (int i = 2; i <= n + 1; ++i) {
            unordered_map<int, int> freq;
            int val = 0;
            for (int j = i - 1; j >= 0; --j) {
                freq[nums[j]]++;
                if (freq[nums[j]] == 2) 
                    val += 2;
                else if (freq[nums[j]] > 2) 
                    ++val;
                dp[i] = min(dp[i], dp[j - 1] + k + val);
            }
        }

        return dp.back();
    }
};

