                else {
                    if (i<n-1) dp[i][j]= min (dp[i][j], dp[i+1][j]+1); //bottom
                    if (j<m-1) dp[i][j]=min (dp[i][j],dp[i][j+1]+1); //right
                }
            }
        }
        return dp;
    }
};
