class Solution {
public:
    int findTheWinner(int n, int k) 
    {
        vector<int> friends;
        for (int i = 1; i <= n; ++i)
            friends.push_back(i);

        int index = 0;
        while (friends.size() != 1)
        {
            // The modulus operator in C++ and many other languages can return a negative result.
            // We need to add the size of array to ensure we are staying within bounds.
            index = ((index + k) % friends.size());

            if (index < 0) 
                index += friends.size();

            friends.erase(friends.begin()+index);
        }

        return friends[0];
    }
};

