class Solution {
public:
    int longestValidSubstring(string word, vector<string>& forbidden) {
        unordered_map<string, int> forbidden_word;
        for (auto it : forbidden) forbidden_word[it] = 1;

        int n = word.size();
        int ans = INT_MIN;
        for(int i=0, j=0; j<n; j++){  // Use of two pointers (i and j) to track the valid substring
            string temp = "";

            if (j - i + 1 > 10) {   // If substring is more than 10 characters, move the start pointer (i)
                i++;
            }

            for(int k=j; k>=i && k>j-10; k--){  // Iterate from current pointer (j) to atmost 10 positions behind current pointer.
                temp = word[k] + temp;

                if(forbidden_word.find(temp) != forbidden_word.end()){   // If the substring is in forbidden list, update the start pointer (i) and break the loop.
                    i = k+1;
                    break;
                }
            }

            ans = max(ans, j-i+1);  // Update the maximum length of valid substring
        }

        return ans == INT_MIN ? -1 : ans;
    }
};

