class Solution {
public:
    // Function to calculate the total amount of money saved.
    int totalMoney(int n) {
        int ans = 0, dif = 28; // Initialize the variables for saving and difference.

        int a = n / 7; // Calculate the number of full weeks in 'n'.

        // Loop to calculate the sum of money saved in each week.
        for(int i=0;i<a;i++) {
            ans += dif;
            dif += 7;
        }

        int b = n % 7; // Calculate the remaining days after full weeks are calculated.
        int ad = 0;

        // Loop to calculate the sum of money saved in the remainder days.
        for(int i=a+1;i<a+b+1;i++) {
            ad += i;
        }

        ans -= ad; // Subtract the sum of money saved in the remainder days from total savings.

        return ans;
    }
};

