class FindElements {
private:
    unordered_set<int> set;
public:
    void recover(TreeNode* root, int x) {
        if (root == nullptr) return;
        root->val = x;
        set.insert(x);
        recover(root->left, 2 * x + 1);
        recover(root->right, 2 * x + 2);
    }

    FindElements(TreeNode* root) {
        recover(root, 0);
    }

    bool find(int target) {
        return set.find(target) != set.end();
    }
};

