class Solution {
public:
    int countWithMergeSort(vector<long> &sums, int left, int right, int lower, int upper)
    {
        if (right - left <= 1) 
        {
            return 0;
        }

        int mid = (left + right) / 2;

        int count = 0;

        // Add this line to ensure the mid element is included in both sides.
        if(lower <= sums[mid] && sums[mid] <= upper)
            count++;

        int leftSideSum = countWithMergeSort(sums, left, mid, lower, upper);
        int rightSideSum = countWithMergeSort(sums, mid, right, lower, upper);

        vector<long> cache(right - left + 1, 0);

        int i = left, j = mid, k = 0;

        while (i < mid && j < right) 
        {
            if (sums[j] <= sums[i]) 
            {
                cache[k++] = sums[j++];
            } 
            else 
            {
                cache[k++] = sums[i++];

                // Count the number of valid range sum.
                while (i < mid && sums[mid] - lower <= sums[i]) i++;
            }
        }

        while(i < mid) 
        {
            cache[k++] = sums[i++];

            // Count the number of valid range sum.
            while (i < mid && sums[mid] - lower <= sums[i]) i++;
        }

        while(j < right) 
        {
            cache[k++] = sums[j++];
        }

        for(int idx = 0; idx < cache.size(); idx++)
        {
            sums[left + idx] = cache[idx];
        }

        return leftSideSum + rightSideSum + count;
