class Solution {
public:
    bool isAnagram(string s, string t) {
        if (s.length() != t.length()) 
            return false;

        vector<int> freqS(26, 0), freqT(26, 0);
        for (char c : s) 
            freqS[c - 'a']++;
        for (char c : t) 
            freqT[c - 'a']++;

        return freqS == freqT;
    }
};

