class Solution {
public:
    int magicalString(int n) 
    {
        string s = "122";
        int i = 2, count = 1;

        while (s.size() < n)
        {
            if (s[i] == '1')
                s += (s.back() == '2' ? "1" : "2");
            else 
                s += (s.back() == '2' ? "11" : "22");

            i++;
        }

        while(i < n) {
            if(s[i] == '1')
                count++;
            i++;
        }

        return count;
    }
};

