            b=min(b,1+func(i,j,obstacles,dp));
         }
        return dp[i][l] = b;
     }

    int minSideJumps(vector<int>& obstacles) {
        int n=obstacles.size();
        vector<vector<int>>dp(n,vector<int>(4,-1));
        return func(0,2,obstacles,dp);
     }
};

