        int leftSideSum = countWithMergeSort(sums, left, mid, lower, upper);
        int rightSideSum = countWithMergeSort(sums, mid, right, lower, upper);


        int i = left;
        int j = mid;

        int n = 0;
        int m = 0;

        vector<long> cache(right - left, 0);

