class Solution {
public:
    int countWithMergeSort(vector<long> &sums, int left, int right, int lower, int upper)
    {
        if(right - left <= 1) 
        {
            return (right - left == 1 && sums[left] >= lower && sums[left] <= upper) ? 1 : 0;
        }

        int mid = (left + right) / 2;

        int count = countWithMergeSort(sums, left, mid, lower, upper) + 
                    countWithMergeSort(sums, mid, right, lower, upper);

        int i = left, j = mid, k = 0, p = mid, q = mid;

        vector<long> cache(right - left, 0);

        while (i < mid) 
        {   
            if (j < right && sums[j] - sums[i] < lower) 
            {
                j++;
            } 

            else 
            {
                p = j;
            }

            if (j < right && sums[j] - sums[i] <= upper) 
            {
                q = j;
                j++;
            } 

            while (k < right - left && k < q - i) 
            {
                cache[k++] = sums[i++];
            }

            count += q - p;
        }

        while(i < mid || j < right)
        {   
            if (j == right || (i < mid && sums[i] <= sums[j])) 
            {
                cache[k++] = sums[i++];
            } 

            else 
            {
                cache[k++] = sums[j++];
            }   
        }

        for(int idx = 0; idx < cache.size(); idx++)
        {
            sums[left + idx] = cache[idx];
        }

        return count;
    }

    int countRangeSum(vector<int>& nums, int lower, int upper) 
    {
        vector<long> prefixSum(nums.size() + 1, 0);

        for (int i = 0; i < nums.size(); ++i)
            prefixSum[i+1] = prefixSum[i] + nums[i];

        return countWithMergeSort(prefixSum, 0, nums.size() + 1, lower, upper);
    }
};

