        if (root == NULL)  // If Node is NULL then we return
            return;
        Traversal(root->left, low, high);  // To check for left Node
        ProcessNode(root, low, high);
        Traversal(root->right, low, high);  // To check for right Node
    }

    void ProcessNode(TreeNode* root, int low, int high) {
        if (root->val >= low && root->val <= high)
            sum += root->val;
    }

    int rangeSumBST(TreeNode* root, int low, int high) {
        Traversal(root, low, high);  // Start the traversal of tree
        return sum;
    }
};

