class FrequencyTracker {
public:
    unordered_map<int, int> numCounts;
    unordered_map<int, int> freqCounts;

    void add(int number) {
        if (numCounts.find(number) != numCounts.end()) {
            freqCounts[numCounts[number]]--;
        }
        ++numCounts[number];
        ++freqCounts[numCounts[number]];
    }

    void deleteOne(int number) {
        if (numCounts.find(number) != numCounts.end() && numCounts[number] > 0) {
            freqCounts[numCounts[number]]--;
            --numCounts[number];
            ++freqCounts[numCounts[number]];
        }
    }

    bool hasFrequency(int frequency) {
        return (freqCounts.find(frequency) != freqCounts.end() && freqCounts[frequency] > 0);
    }
};

