class Solution {
public:
    unordered_map<long long int,long long int>mp;

    void updateMp(int n, int divisor){
        while((n % divisor) == 0){
            mp[divisor]++;
            n = n / divisor;
        }
        if(n > divisor){
            mp[n]++;
        }
    }

    int trailingZeroes(int n) {
        for(int i=2;i*i<=n;i++){
            updateMp(n, i);
        }

        return min(mp[2],mp[5]); 
    }
};

