        return (n*fact(n-1))%1000000007; // Change the argument to n-1
    }

    int numPrimeArrangements(int n) {
        if(n==1)return 1; // Changed the assignment operator '=' to comparison operator '=='
        if(n<=3)return n+1; // Changed from 'n-1' to 'n+1' as we need arrangements instead of primes
        int t=0,flag;

        for(int i=2;i<=n;i++) {
            flag=0;

            for(int j=2;j*j<=i;j++) { // Changed the condition in loop to 'j*j<=i'
                if(i%j==0) {
                    flag=1;
                    break;
                }
            }

            if(flag==0) t++;
        }

        return (fact(t)*fact(n-t))%1000000007;
    }
};

