class Solution {
public:
    int countWithMergeSort(vector<long> &sums, int left, int right, int lower, int upper)
    {
        if (right - left <= 1) 
            return (left == right ? 0 : (lower <= sums[left] && sums[left] <= upper));

        int mid = (left + right)/2;
        int count = 0;
        vector<long> cache(right - left, 0);

        count += countWithMergeSort(sums, left, mid, lower, upper);
        count += countWithMergeSort(sums, mid, right, lower, upper);

        int j = mid, k = 0;
        for (int i = left; i < mid; ++i) {
            while (j < right && sums[j] - sums[i] <  lower) j++;
            while (j < right && sums[j] - sums[i] <= upper) j++, count += k;
            while (k < cache.size() && (sums[left + k] < sums[i] || (sums[left + k] == sums[i] && i > left + k))) 
                sums[left + k] = cache[k], k++;

            cache[--k] = sums[i];
        }

        while(--k >= 0) 
            sums[left + k] = cache[k];

        return count;
    }

    int countRangeSum(vector<int>& nums, int lower, int upper) {
        vector<long> prefixSum(nums.size() + 1, 0);

        for (int i = 0; i < nums.size(); ++i)
            prefixSum[i+1] = nums[i] + prefixSum[i];

        return countWithMergeSort(prefixSum, 0, nums.size() + 1, lower, upper);
    }
};

