class Solution {
public:
    int dp[105][10005];

    int helper(int i, vector<int>& arr, int sum) 
    {
        if (sum == 0)
            return 0;

        if (i > arr.size() || sum < 0)
            return INT_MAX - 1000;

        if (dp[i][sum] != -1)
            return dp[i][sum];

        int mini = INT_MAX;

        if (arr[i-1] <= sum) 
            mini = min(mini, 1 + helper(i, arr, sum - arr[i-1]));

        mini = min(mini, helper(i + 1, arr, sum));

        return dp[i][sum] = mini;
    }

    int numSquares(int n) {
        vector<int> arr;
        for (int i = 1; i * i <= n; ++i) 
            arr.push_back(i*i);

        memset(dp, -1, sizeof(dp));

        return helper(0, arr, n);
    }
};

