class Solution {
public:
    int dir[4][2] = {{0, 1}, {0, -1}, {-1, 0}, {1, 0}};

    int shortestPathAllKeys(vector<string>& grid) {
        int m = grid.size(), n = grid[0].size();
        queue<tuple<int, int, int>> que;
        vector<vector<unsigned long>> mapKey(m, vector<unsigned long>(n, 0));
        int target = 0;

        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                if ('a' <= grid[i][j] && grid[i][j] <= 'f') {
                    target |= 1 << (grid[i][j] - 'a');
                } else if (grid[i][j] == '@') {
                    que.emplace(i, j, 0);
                    mapKey[i][j] = 1;
                }
            }
        }

        int step = 0;
        while (!que.empty()) {
            int size = que.size();

            for (int s = 0; s < size; ++s) {
                auto [i, j, key] = que.front();
                que.pop();

                if ('a' <= grid[i][j] && grid[i][j] <= 'f') {
                    key |= 1 << (grid[i][j] - 'a');
                    if (key == target) return step;
                    mapKey[i][j] |= 1l << key;
                }

                for (int d = 0; d < 4; ++d) {
                    int newi = i + dir[d][0], newj = j + dir[d][1];

                    if (!(0 <= newi && newi < m && 0 <= newj && newj < n)) continue;

                    char val = grid[newi][newj];
                    if (val == '#' || 'A' <= val && val <= 'F' && !(key & (1 << (val - 'A')))) continue;

                    if (mapKey[newi][newj] & 1l << key) continue;

                    mapKey[newi][newj] |= 1l << key;  
                    que.emplace(newi, newj, key);
                }
            }
            ++step;
        }

        return -1;
    }
};

