class Solution {
public:
    int longestIdealString(string s, int k) 
    {
        // 1. Tabulation Method || Bottom Up Approach
        vector<int> dp(27, 0);

        for (char& c : s)
        {
            int index = c - 'a';

            int op1 = 0;
            if (dp[index] != -1)
                op1 = 1 + dp[index];

            int op2 = 1;
            for (int i = max(0, index-k); i <= min(25, index+k); ++i) 
                if (dp[i] != -1 && abs(index - i) > k)
                    op2 = max(op2, dp[i] + 1);

            dp[index] = max(op1, op2);
        }

        return *max_element(dp.begin(), dp.end());
    }
};

