class Solution {
public:
    int closetTarget(vector<string>& words, string target, int startIndex) {
        auto it = find(words.begin(), words.end(), target);
        if (it == words.end()) return -1;

        int n = words.size();
        int ind = it - words.begin();
        if (words[startIndex] == target) return 0;

        int i = startIndex, j = startIndex, cnt1 = 0, cnt2 = 0;
        while ((i >= 0 || j < n)){
            if ((i >= 0 && words[(i+n)%n]==target) || (j<n && words[j%n]==target)) {
                return min(cnt1, cnt2);
            }

            i = (i - 1 + n) % n;
            j = (j + 1) % n;
            if ((i != startIndex && words[i] != target) || (j != startIndex && words[j] != target)) {
                cnt1++, cnt2++;
            }
        }

        return -1;
