class Solution {
public:

    int countWithMergeSort(vector&sums, int left, int right, int lower, int upper)
    {
        if (right - left <= 1) 
        {
            return (left == right ? (lower <= sums[left] && sums[left] <= upper) : 0);
        }

        int mid = (left + right)/2;

        int count = countWithMergeSort(sums, left, mid, lower, upper) + countWithMergeSort(sums, mid, right, lower, upper);

        vector<long> cache(right - left + 1);

        for (int i = left, j = mid, k = 0; i < mid; ++i) 
        {
            while (j < right && sums[j] - sums[i] <  lower) ++j;
            while (j < right && sums[j] - sums[i] <= upper) cache[k++] = sums[j++];

            count += j - mid;
        }

        merge(sums.begin() + left, sums.begin() + mid, sums.begin() + mid, sums.begin() + right, cache.begin());

        copy_n(cache.begin(), right - left, sums.begin() + left);

        return count;
    }

    int countRangeSum(vector<int>& nums, int lower, int upper) {

        vector<long> prefixSum(nums.size());

        partial_sum(nums.begin(), nums.end(), prefixSum.begin(), plus<long>());

        return countWithMergeSort(prefixSum, 0, prefixSum.size(), lower, upper);
    }
};

