public:
    string smallestString(string s) {
        int temp = -1;
        int cnt = 0;
        for(int i = 0; i < s.size(); i++)
        {
            if(s[i] == 'a')
            {
                cnt++;
            }
        }
        if(cnt == s.size())
        {
            s[s.size() - 1] = 'z';
            return s;
        }
        else
        {
            for(int i = 0; i < s.size(); i++)
            {
                if(s[i] == 'a')
                {
                    temp = i;
                    break;
                }
            }

            if(temp == 0)
            {
                int temp1 = 1, temp2 = 1;
                for(int i = 1 ; i < s.size(); i++)
                {
                    if(s[i] != 'a')
                    {
                        temp1 = i;
                        break;
                    }
                }
                for(int i = temp1; i < s.size(); i++)
                {
                    if(s[i] == 'a')
                    {
