        for (int i = 0, j = 0; j < N; ++j) {
            while (stones[j] - stones[i] + 1 > N) 
                ++i;

            if(j-i+1 == N-1 && stones[j]-stones[i]+1 == N-1)
                low = min(low, 2);
            else
                low = min(low, N - (j - i + 1));
        }

        int high = 1 + max((stones[N-1] - stones[1]), (stones[N-2] - stones[0])) - N;  

        return {low, high};
    }
};

