class DisjointSet{
public:
    vector<int>par,size;
    DisjointSet(int n){
        par.resize(n+1);
        size.resize(n+1,1);
        for(int i=0; i<=n; i++){
            par[i] = i;
         }
     }
    int findpar(int u){
        return par[u] == u ? u : (par[u] = findpar(par[u])); 
     }

    void unionBySize(int u, int v){
        u = findpar(u);
        v = findpar(v);

        if(u == v) return;

        if(size[u] >= size[v]){
            par[v] = u;
            size[u] += size[v];
         }
        else{
            par[u] = v;
            size[v] += size[u];
         }
     }
};
