        for(int i=len-1;i>=0;i--)
        {
            int num = 0;
            for(int j=1;j<=min(len-i,31);j++)
            { 
                num = num*2 + (temp[i+j-1] == "1" ? 1 : 0);
                if(0 < num && num <= n) seen.insert(num);
             }
            if(seen.size() != n) return true;
        }
        return false;
    }
