class Solution {
public:
    bool queryString(string s, int n) {
      unordered_set<int> seen;
      int len = s.length();
      for (int i = 0; i < len; ++i) {
        if (s[i] == '1') {
          int num = 1;
          seen.insert(num);
          for (int j = i + 1; j < min(len, i + 32); ++j) {
            num = (num << 1) + (s[j] - '0');
            if (num <= n && !seen.count(num)) seen.insert(num);
          }
        }
      }
      return seen.size() == n + 1;
    }
};

