        // Sum up the output probabilities of all cells at the kth move to get the final output probability.
        for (int r = 0; r < n; r++) {
            for (int c = 0; c < n; c++) {
                output += dp[k][r][c];
            }
        }
        // return the output that the knight remains on the board after k moves.
        return output;
    }
}

