class Solution {
    public int[] distinctDifferenceArray(int[] nums) {
        int n=nums.length;
        int ans[]=new int[n];

        for(int i=0;i<n;i++){
            Set<Integer>set1=new HashSet<>();
            Set<Integer>set2=new HashSet<>();

            for(int j=0;j<=i;j++){
                set1.add(nums[j]);
            }
            for(int j=i+1;j<n;j++){
                set2.add(nums[j]);
            }

            ans[i]= set1.size() - set2.size();
        }
        return ans;
    }
}

