class Solution:
    def numMovesStonesII(self, stones: List[int]) -> List[int]:
        stones.sort()
        stone_length = len(stones)
        move_penultimate = stones[-2] - stones[0] - stone_length + 2
        move_final = stones[-1] - stones[1] - stone_length + 2
        most_moves = max(move_penultimate, move_final)
        if move_penultimate == move_final == 0:
            return [0, 0]
        max_legal_moves = 0
        starting_index = 0
        for index in range(stone_length):
            while stones[index] - stones[starting_index] >= stone_length:
                starting_index += 1
            max_legal_moves = max(max_legal_moves, index - starting_index + 1)
        min_legal_moves = stone_length - max_legal_moves
        if stones[max_legal_moves - 1] - stones[starting_index] == stone_length - 1 and stones[max_legal_moves] - stones[starting_index] == 2:
            min_legal_moves = min(min_legal_moves, 2)
        return [min_legal_moves, most_moves]

