class Solution {
public:
    TreeNode* reverseOddLevels(TreeNode* root) {
        queue<TreeNode*> q;
        q.push(root);
        int level = 0;
        while(!q.empty()){
            vector<TreeNode*> tmp;
            int n = q.size();
            for(int i=0; i<n; i++){
                node = q.front();q.pop();
                if(level%2!=0){
                    // swap values
                    if(node->left)
                        q.push(node->right);
                    if(node->right)
                        q.push(node->left);
                }else{
                    if(node->left)
                        q.push(node->left);
                    if(node->right)
                        q.push(node->right);
