class Solution {
public:
    int distanceBetweenBusStops(vector<int>& distance, int start, int destination) {
        int i, dis1=0, dis2=0;

        if(start>destination)
        {
            swap(start, destination);
        }

        for(i=start ; i<=destination ; i++)
        {
            dis1 += distance[i];
        }

        for(i=destination-1; i>=start; i--)
        {
            dis2 += distance[i];
        }

        return min(dis1, dis2);
    }
};

