class Solution {
public:
    bool validPath(int n, vector<vector<int>>& edges, int source, int destination) {
        unordered_map<int,vector<int>> mpp;
        unordered_map<int,bool>vis;

        for(auto it : edges)
        {
            mpp[it[0]].push_back(it[1]);
            mpp[it[1]].push_back(it[0]);
            vis[it[0]] = false;
            vis[it[1]] = false;
        }

        queue<int>q;
        q.push(source);

        while(!q.empty())
        {
            int temp = q.front();
            q.pop();

            if(temp == destination) return true;

            for(int i = 0;i<mpp[temp].size();++i) 
            {
                if(vis[mpp[temp][i]]==false)
                {
                    q.push(mpp[temp][i]);
                    vis[mpp[temp][i]] = true;
                }
            }

        }

        return false;
    }
};

