        for(int i=0;i<n/2;i++)
            A.push_back(nums[i]);
        for(int i=n/2;i<n;i++)
            B.push_back(nums[i]);

        vector<int>sumA,sumB;
        find(A,0,A.size(),0,sumA);
        find(B,0,B.size(),0,sumB);

        sort(sumA.begin(),sumA.end());
        sort(sumB.begin(),sumB.end());

        minAbsDifference(A, B);

    }

    void minAbsDifference(vector<int>& sumA, vector<int>& sumB){
        int ans=INT_MAX;
        int goal = 0;

        for(int i=0;i<sumA.size();i++){
            int s=sumA[i];
            int l=0;
            int r=sumB.size()-1;
            while(l<=r){
                int mid=l+(r-l)/2;
                int sum=s+sumB[mid];
                if(sum==goal)
                    return 0;
                ans=min(ans,abs(sum-goal));
                if(sum>goal){
                    r=mid-1;
                }
                else{
                    l=mid+1;
                }    
            }
        }

        return ans;
    }
};

