class FrequencyTracker {
public:
    unordered_map<int,int> x,y;
    void add(int number){ 
        x[number]++;
        if(x.find(number)!=x.end()&&x[number]!=1) y[x[number]-1]--;
        if(y.find(x[number])!=y.end()) y[x[number]]--;
    }
    void deleteOne(int number){ 
        if(x.find(number)!=x.end()){
            x[number]--;
            if(y.find(x[number]+1)!=y.end()) y[x[number]+1]--;
            if(y.find(x[number])!=y.end()) y[x[number]]++;
        }
    }
    bool hasFrequency(int frequency){ 
        return y.find(frequency)!=y.end() && y[frequency]!=0;
    }
};

