    vector<vector<pair<int,int>>>graph(n);
    for(auto edge: redEdges)
    {
        graph[edge[0]].push_back({edge[1],0});
    }
    for(auto edge: blueEdges)
    {
        graph[edge[0]].push_back({edge[1],1});
    }
    vector<int>ans(n,-1);
    for(int i=0;i<n;i++)
    {
        queue<pair<int,int>>visit;
        visit.push({i,0});//node and color
        ans[i]=0;
        while(!visit.empty())
        {
            int n=visit.size();
            for(int j=0;j<n;j++)
            {
                pair<int,int>x=visit.front();
                visit.pop();
                for(auto neighbor:graph[x[0]])
                {
                    if(neighbor[1]==1-x[1])
                    {
                        if(ans[neighbor[0]]==-1)
                        {
                            ans[neighbor[0]]=ans[x[0]]+1;
                            visit.push({neighbor[0],neighbor[1]});
                        }
                    }
                }
            }
        }
    }
    return ans;
