class Solution {
public:
    TreeNode* reverseOddLevels(TreeNode* root) {
        queue<TreeNode*> q;
        q.push(root);
        int level = 1;
        vector<TreeNode*> tmp;
        while(!q.empty()){
            tmp.clear();
            int n = q.size();
            for(int i = 0; i < n; i++){
                TreeNode* node = q.front(); q.pop();
                if(level % 2 != 0)
                    tmp.push_back(node);
                if(node->left) q.push(node->left);
                if(node->right) q.push(node->right);
            }
            for(int i = 0; i < tmp.size() / 2; i++){
                swap(tmp[i]->val, tmp[tmp.size()-1-i]->val);
            }
            level++;
        }
        return root;
    }
