                int op1 = 0 + next[prev];
                int op2 = 0 ;

               if(prev == 26)
               op2 = 1 + next[s[index]-'a'];
               else if(abs(s[index]-'a' - prev) <= k)
               op2 = 1 + next[s[index]-'a'];

               curr[prev] = max(op1 , op2);
            }
            next = curr;
        }

       return max(curr[s[0]-'a'] , curr[26]);
