    if (ex > 0) {
        int diff = 40 + nCost(m, n, i, j, mask_in, mask_ex, 20);
        res = max(res, diff + dfs(m, n, p + 1, in, ex - 1, n_mask_in, n_mask_ex + 1));
    }
    dp[p][in][ex][mask_in][mask_ex] = res + 1;
    return res;
}
