public:
    vector<int> longestObstacleCourseAtEachPosition(vector<int>& nums) {

        int n = nums.size();

        // lis store elements of longest increasing subsequence till ith

        vector<int> lis;

        // ans[i] store, no of elements satisfying the condition including ith

        vector<int> ans;

        for(int i = 0; i < n; i++)
