class Solution {
public:
    void rotate(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> temp(nums.begin(), nums.end());
        for(int i=0;i<k;i++){
            swap(temp[0], temp[n-1]);
            rotate(temp.begin()+1, temp.begin()+(n-2));
        }
        nums.assign(temp.begin(),temp.end());
    }
};

