class Solution {
public:
    string smallestString(string s) {
        int a_count = 0, non_a_count = 0;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == 'a') a_count++;
            else non_a_count++;
        }
        char arr[a_count];
        int j = 0;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == 'a') arr[j++] = 'a';
            else arr[j++] = (char)(s[i] - 1);
        }
        sort(arr, arr+a_count);
        string res = "";
        j = 0;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == 'a') res += "a";
            else res += (char)(arr[j++] + 1);
        }
        return res;
    }
};

