class Solution {
public:
    string longestCommonPrefix(vector<string>& strs) {
        if(strs.empty()) return "";
        sort(strs.begin(), strs.end());
        string ans = strs[0];
        for(int i=1; i<strs.size(); i++) {
            while(ans.back() != strs[i][0]) 
                ans.pop_back();
            if(ans.empty()) return "";
            ans = ans.substr(0, strs[i].find_first_not_of(ans) );
        }
        return ans;
    }
};

