class Solution {
public:
    vector<int> findDisappearedNumbers(vector<int>& nums) {
        int n = nums.size();
        for (int i=0; i<n; i++) {
            while (nums[i] != i+1 && 0 <= nums[i] && nums[i] < n)
                swap(nums[i], nums[nums[i]]);
        }

        vector<int> res;
        for (int i=0; i<n; i++)
            if (!binary_search(res.begin(), res.end(), i+1))
                res.push_back(i+1);

        return res;
    }
};

