        u = findpar(u);
        v = findpar(v);

        if(u == v) return;

        if(size[u-1] >= size[v-1]){
            par[v-1] = u;
            size[u-1] += size[v-1];
        }
        else{
            par[u-1] = v;
            size[v-1] += size[u-1];
        }
